function SerialNumbers(serial_ID, Name) {

var SerialNumbers = global.get("SerialNo") || {}

var noItem = false

/////////////// Action Manifold ///////////////
for (let [key, value] of Object.entries(SerialNumbers)) {
    let serial = key;

    if (serial == serial_ID && Name != value) {
        // Name Changed - Now Update Name
        SerialNumbers[serial_ID] = Name
        global.set("SerialNo",SerialNumbers)

    }
    // Check for new Devices
    if (serial == serial_ID) {
        noItem = true
    }
}

// If a new device is detected, add to list
if (!noItem)
{
    SerialNumbers[serial_ID] = Name
    global.set("SerialNo",SerialNumbers)
}

// First index to create object Array
var index = 0;

// Prepare list format
for (let [key, value] of Object.entries(SerialNumbers)) {
    let label = value + " : " + key
    if (index === 0) {
        msg.options = [{ [label] : key }];
    } else {
        msg.options.push({ [label] : key });
    }
    index++;

}

