//List of files from RPI Github
var firmwares = msg.files;
//TRUC_VERSION DATA from ESP
var version = msg.req.headers;
//Version Data
var currentFile = version["x-esp32-version"];
//Extract Device Type PowerPro or LightPro
var deviceType = currentFile.substring(0,currentFile.indexOf("V")+1)
msg.deviceType = deviceType;

//Extracts Github file that matches current ESP version ID
var existingFile = firmwares.filter(item=> item.includes(currentFile)).pop();
//Filters out any incorrect device types ie PowerPro or LightPro
var firmwareNames = firmwares.filter(item=> item.includes(msg.deviceType));
//Sorts out list of files
var comparer = new Intl.Collator(undefined, {numeric: true, sensitivity: 'base'});
firmwareNames.sort(comparer.compare)[firmwareNames.length-1]

var mostRecentFile;
//Pops off most recent file
msg.mostRecentFile = firmwareNames.pop();
msg.existingFile = existingFile;
//Compares ESP version to most recent developed file
if((msg.existingFile.localeCompare(msg.mostRecentFile)) < 0)
{
    msg.filename = "/home/pi/Firmware/" + msg.mostRecentFile;
}
else
{
    msg.filename = undefined;
}

return msg;
